
public class kalkulator {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
double a = 12.34;
double b = 56.78;

System.out.println("a + b = " + (a+b));
System.out.println("a - b = " + (a-b));
System.out.println("a * b = " + (a*b));
System.out.println("a / b = " + (a/b));
System.out.println("a > b ?" + (a>b));
System.out.println("a * b > 100? " + (a*b>100));
	}

}
